import React, {useContext} from 'react';
import './Homepage.css';
import userContext from './userContext';
import {Link} from 'react-router-dom';


function Homepage() {
  const { currentUser } = useContext(userContext);
  console.log("Home", currentUser)
  return (
    <>
      <h1>Jobly</h1>
      {currentUser.username ? <h2 id='homepageP'>Welcome back, {currentUser}!</h2> :
      <div >
        <Link className="home-button btn btn-primary font-weight-bold mr-3"
          to="/login">Log in
        </Link>
        <Link className="home-button btn btn-primary font-weight-bold"
          to="/signup">Sign Up</Link>
      </div>}
      <p id='homepageP'>Welcome back, {currentUser}! Where you can find all of the jobs in one place!</p>
    </>
  )
}

export default Homepage;

