return (
  <>
    <h1>Jobly</h1>
    {currentUser ? (
      <>
        <h2 id='homepageP1'>Welcome back, {currentUser}!</h2>
        <p id='homepageP'>Where you can find all of the jobs in one place!</p>
      </>
    ) : (
      <div id='homepageP1' className='homepageDiv'>
        <Link className="home-button btn btn-primary font-weight-bold mr-3" to="/login">
          Log in
        </Link>
        <Link className="home-button btn btn-primary font-weight-bold" to="/signup">
          Sign Up
        </Link>
        <p id='homepageP'>Where you can find all of the jobs in one place!</p>
      </div>
    )}
  </>
);
}


export default Homepage;

